BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "school_detail" (
	"school_no"	INTEGER NOT NULL,
	"school_webpage"	TEXT NOT NULL,
	"school_name"	TEXT NOT NULL,
	"school_type"	TEXT DEFAULT  ,
	"domain"	TEXT NOT NULL,
	"grade_min"	TEXT DEFAULT  ,
	"grade_max"	TEXT DEFAULT  ,
	"solution"	TEXT NOT NULL,
	"solution_type"	TEXT NOT NULL,
	"curriculum_type"	TEXT NOT NULL,
	PRIMARY KEY("school_no")
);
CREATE TABLE IF NOT EXISTS "school_type" (
	"seq_no"	INTEGER NOT NULL,
	"grade"	TEXT NOT NULL,
	"school_type"	TEXT NOT NULL,
	PRIMARY KEY("seq_no")
);
CREATE TABLE IF NOT EXISTS "subject_domain" (
	"seq_no"	INTEGER NOT NULL,
	"course"	TEXT NOT NULL,
	"domain"	TEXT NOT NULL,
	PRIMARY KEY("seq_no")
);
CREATE TABLE IF NOT EXISTS "solution_research" (
	"school_name"	TEXT NOT NULL,
	"solution"	TEXT NOT NULL,
	"research_page"	TEXT DEFAULT N/A,
	PRIMARY KEY("school_name","solution","research_page")
);
CREATE TABLE IF NOT EXISTS "academic_planning_guides" (
	"state_jurisdiction"	TEXT NOT NULL,
	"local_district"	TEXT NOT NULL,
	"organization"	TEXT NOT NULL,
	"link"	TEXT NOT NULL,
	"grade_range"	TEXT NOT NULL,
	"type"	TEXT NOT NULL,
	"tag"	TEXT NOT NULL,
	"giapii_rating"	INTEGER NOT NULL,
	PRIMARY KEY("local_district","organization","grade_range","tag")
);
CREATE TABLE IF NOT EXISTS "iap_guidelines" (
	"section"	INTEGER NOT NULL,
	"subsection"	INTEGER NOT NULL,
	"description"	TEXT NOT NULL,
	"to_be_completed_by"	TEXT NOT NULL,
	"more_information"	TEXT NOT NULL,
	PRIMARY KEY("section","subsection")
);
CREATE TABLE IF NOT EXISTS "iap_guidelines_resources" (
	"seq_no"	INTEGER NOT NULL,
	"section"	INTEGER NOT NULL,
	"subsection"	INTEGER NOT NULL,
	"description"	TEXT NOT NULL,
	"reference"	TEXT NOT NULL,
	FOREIGN KEY("subsection") REFERENCES "iap_guidelines"("subsection"),
	FOREIGN KEY("section") REFERENCES "iap_guidelines"("section"),
	PRIMARY KEY("seq_no","section","subsection")
);
CREATE TABLE IF NOT EXISTS "solution" (
	"solution_no"	INTEGER NOT NULL,
	"solution"	TEXT NOT NULL,
	"school_name"	TEXT NOT NULL,
	"solution_type"	TEXT NOT NULL,
	FOREIGN KEY("school_name") REFERENCES "school_detail"("school_name"),
	FOREIGN KEY("solution") REFERENCES "school_detail"("solution"),
	PRIMARY KEY("solution_no")
);
