	"school_no"
);
CREATE INDEX IF NOT EXISTS "idx_school_2" ON "school_detail" (
	"school_no",
	"school_name",
	"solution"
);
CREATE INDEX IF NOT EXISTS "idx_school_3" ON "school_detail" (
	"school_no",
	"school_name",
	"school_type"
);
CREATE INDEX IF NOT EXISTS "idx_solution_research_1" ON "solution_research" (
	"school_name",
	"solution",
	"research_page"
);
CREATE INDEX IF NOT EXISTS "idx_apg_1" ON "academic_planning_guides" (
	"local_district",
	"grade_range",
	"tag"
);
CREATE INDEX IF NOT EXISTS "idx_solution_1" ON "solution" (
	"solution_no"
);
CREATE INDEX IF NOT EXISTS "idx_solution_2" ON "solution" (
	"solution_no",
	"school_name",
	"solution"
);
CREATE INDEX IF NOT EXISTS "idx_solution_3" ON "solution" (
	"solution_no",
	"school_name",
	"solution_type"
);
COMMIT;
