INSERT INTO "iap_guidelines" ("section","subsection","description","to_be_completed_by","more_information") VALUES (0,0,'Below is a proposed set of guidelines for parents, teachers, and tutors to use in the creation of a students'' individualized academic plan',' ',' ');
